import chai, {expect} from 'chai'
import chaiHttp from 'chai-http'

chai.use(chaiHttp);

var host = "https://pestomeet-backend.herokuapp.com"
let should = chai.should();


describe('Book Event Route Testing', function() {
    it('should send success message for event creation', function(done) {
        let eventId="c602698f-7567-4804-99cc-5e170d9d49ce"
        chai.request(host).post('/api/pesto/book/event/'+eventId)
            .send({
                "hasBooked":true,
                "attendees":[]
                })
            .set({"Content-Type":"application/json"})
            .set({"Authorization":"Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..sJ5OkE9AHN8r-VeG7YsfUTRj0Usmit-IZKjsPqv5QEU"})
            .end(function(err, res) {
                if(err){
                    done(err)
                }else{
                res.should.have.status(200);
                res.body.should.be.a('object');
                res.body.should.have.property('message').to.be.oneOf(["Event Booked Successfully","Couldn't Find the Event","Update Failed ! Please Try Again"])
                res.body.should.have.property('result')
                res.body.should.have.property('statusCode').to.be.oneOf([true,false]);
                done();
                }
            });
        
    }).timeout(5000);
        
    });



